mapper.update:
UPDATE product
prod_name = #{prodName} , prod_detail = #{prodDetail}, manufacture_day = #{manuDate} , price =#{price}, image_file =#{fileName}, quantity =#{quantity}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT prod_no, prod_name, prod_detail, manufacture_day, price, image_file, reg_date, quantity FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT prod_no, prod_name, prod_detail, manufacture_day, price, image_file, reg_date, quantity FROM product
AND p.prod_no = #{searchKeyword}
AND p.prod_name LIKE '%'||#{searchKeyword}||'%'
AND p.price = #{searchKeyword}
ORDER BY prod_no reg_date desc price price desc ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT prod_no, prod_name
					FROM product
					
						
							
				 				AND prod_no = #{searchKeyword}
							
							
				 				AND prod_name LIKE '%'||#{searchKeyword}||'%'
							
							
				 				AND price = #{searchKeyword}
							
						
					 ) countTable
