mapper.update:
UPDATE transaction
payment_option = #{paymentOption} , receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, demailaddr =#{divyAddr}, dlvy_request =#{divyRequest}, dlvy_date =#{divyDate}, quantity =#{quantity}
WHERE tran_no = #{tranNo}

mapper.select:
SELECT tran_no , prod_no , buyer_id , payment_option , receiver_name, receiver_phone, demailaddr, dlvy_request, tran_status_code,order_data,dlvy_date, quantity FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT * FROM transaction
buyer_id =#{buyerId}
ORDER BY tran_no DESC ) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}

  	SELECT COUNT(*)
  	FROM(	SELECT *
					FROM transaction
						 
						buyer_id= #{buyerId}	
						
						
					 ) countTable
